import React, {useState, useEffect} from "react";
//import { professores } from "./data";
import ProfessorTableRow from "./ProfessorTableRow";
import axios from "axios";

const ListProfessor = () => {

    const [professores, setProfessores] = useState([]);

    useEffect(
        ()=> {
            axios.get('http://192.168.1.9:3001/professores')
            .then(
                (res)=> {setProfessores(res.data)}
            )
            .catch(
                (err)=>{console.log(err)}
            )
        },[]
    );

    const generateTable = () => {
        if (!professores) return;
        return professores.map((professor, i) => {
            return <ProfessorTableRow professor={professor} key={i} />;
    });
    } 

    return (
        <div>
            <h2>Listar Professor</h2>
            <table className="table table-success table-striped">
                <thead>
                    <th>ID</th>
                    <th>Nome</th>
                    <th>Universidade</th>
                    <th>Titulação</th>
                    <th colSpan="2"></th>
                </thead>
                <tbody>
                    {generateTable()}
                </tbody>
            </table>
        </div>
    )
}

export default ListProfessor;
